
public class Ejercicio1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int iNum1 = 10;
		int iNum2 = 7;
		double dSuma, dResta, dMult, dDiv;
		
		dSuma = iNum1+iNum2;
		dResta = iNum1-iNum2;
		dMult = iNum1*iNum2;
		dDiv = iNum1/iNum2;
		
		System.out.println("Los numeros ingresados son: "+iNum1+" - "+iNum2);
		System.out.println("La suma es: "+dSuma);
		System.out.println("La resta es: "+dResta);
		System.out.println("La multiplicacion es: "+dMult);
		System.out.println("La division es: "+dDiv);

	}

}
